import React, { useState } from 'react';
import './css/preferences.css';
import { useSpring, animated } from 'react-spring';

const selected = [];

function addOption(selectedChoice, buttonSelected) {
    if(selected.includes(selectedChoice))
    {
        for(var i = 0; i < selected.length; i++)
        {
            if(selected[i] === selectedChoice) {
                selected.splice(i, 1);
                break;
            }
        }
        //buttonSelected.style["background-color"] = "rgb(182, 182, 182)";
    }
    else
    {
        selected[selected.length] = selectedChoice;
        //buttonSelected.style["background-color"] = "rgb(85, 85, 85)";
    }
};

function ListElements() {
    <div>
        <li className="preferenceChoice"><button type="button" className="optionButton" onClick={addOption('Music', this)}>Music</button></li>
        <li className="preferenceChoice"><button type="button" className="optionButton">Shopping</button></li>
        <li className="preferenceChoice"><button type="button" className="optionButton">Reading</button></li>
        <li className="preferenceChoice"><button type="button" className="optionButton">Writing</button></li>
        <li className="preferenceChoice"><button type="button" className="optionButton">Coffee</button></li>
        <li className="preferenceChoice"><button type="button" className="optionButton">Sports</button></li>
        <li className="preferenceChoice"><button type="button" className="optionButton">Video Games</button></li>
        <li className="preferenceChoice"><button type="button" className="optionButton">Food</button></li>
        <li className="preferenceChoice"><button type="button" className="optionButton">Board Games</button></li>
        <li className="preferenceChoice"><button type="button" className="optionButton">Movies</button></li>
        <li className="preferenceChoice"><button type="button" className="optionButton">Hiking</button></li>
    </div>
}

function Options() {

    const props = useSpring({
        from: { y: 700, opacity: 0 },
        to: {y: 200, opacity: 1},        
    })

    return(
        <animated.div style={props} className="options">
            <ul className ="choiceList">

            </ul>
        </animated.div>
    );
};

function IntroText() {
    const props = useSpring({
        from: { y: 700, opacity: 0 },
        to: {y: 150, opacity: 1},        
    })

    return(
        <div className="introText">
            <animated.h1 style={props} id="welcomeMessage">Welcome firstName lastName!</animated.h1>
            <animated.h2 style={props} id="welcomeParagraph">Let's get to know you better, select at least 3 of the following general interests:</animated.h2>
        </div>
    );

}

function Preferences() {
    const[on, set] = React.useState(true);

    return(
        <div className="preferencesPage">
            <IntroText />
            <Options />
        </div>
    );
};

export default Preferences;
