import React from 'react'
import ReactMapGL, { Marker, Popup } from 'react-map-gl'
import { useState, useEffect } from 'react';
import "../components/css/map.css";
import { AirportShuttle, Room, Star } from '@material-ui/icons'
import axios from 'axios';
// import { format } from "timeago.js";

function Map() {
    const [events, setEvents] = useState([]);
    const [currentPlaceId, setCurrentPlaceId] = useState(null);
    const [newPlace, setNewPlace] = useState(null);
    const [viewPort, setViewPort] = useState({
        latitude: 28.60236,
        longitude: -81.20008,
        // width: '100vw',
        // height: '100vh',
        width: window.innerWidth,
        height: window.innerHeight,
        zoom: 14
    });

    // gets all the events from the database and displays them on the map
    useEffect(() => {
        const getEvents = async () => {
            try{
                const res = await axios.get("http://localhost:5000/api/events");
                console.log(res.data);
                setEvents(res.data);
            } catch(err) {
                console.log(err);
            }
        };
        getEvents();
    }, []);

    const handleMarkerClick = (id, lat, long) => {
        setCurrentPlaceId(id);
        setViewPort({...viewPort, latitude: lat, longitude: long, zoom: 16});
    };

    const handleAddClick = (event) => {
        const [long,lat] = event.lngLat;
        setNewPlace({
            lat,
            long,
        });
    };

    return (
        <div className="map">
            <ReactMapGL

                // The following three lines of code displays the map along with the appropriate styling.
                mapboxApiAccessToken={"pk.eyJ1IjoiZWxyNDI0IiwiYSI6ImNrcXM5cGdvZTFtbjUybnFsZTF1N2c5bmkifQ.1e0zeu37TrBHe_TiqmjNVw"}
                mapStyle="mapbox://styles/elr424/ckqsacra23gy117r0j9otwx4x"
                {...viewPort}

                // Enables the user to move around on the map. Only temporary.
                onViewportChange={(newView) => setViewPort(newView)}
                onDblClick = {handleAddClick}
            >
                {/* Places all events stored in the database onto the map*/}
                {events.map(events =>(
                    <>
                    <Marker
                        latitude={events.lat}
                        longitude={events.long}
                        offsetLeft={-viewPort.zoom*1.5}
                        offsetTop={-viewPort.zoom*3}
                    >
                        <Room
                            style={{
                                fontSize: viewPort.zoom * 3,
                                color: "red",
                                cursor: "pointer"
                            }}
                            onClick={() => handleMarkerClick(events._id, events.lat, events.long)}
                        />
                    </Marker>
                    {events._id == currentPlaceId &&
                    <Popup
                        latitude={events.lat} 
                        longitude={events.long}
                        closeButton={true}
                        closeOnClick={false}
                        anchor="left"
                        onClose={() => setCurrentPlaceId(null)}
                    >
                        <div className="card">
                            {/* <label>Place</label> */}
                            <h4 className="place">Title</h4>
                            {/* <label>Review</label> */}
                            <p className="desc">Description</p>
                            {/* <label className="rating">Rating</label> */}
                            <div className="stars">
                                {/* The Array() function below takes p.rating
                                    as the number cells in the array and the .fill() method 
                                    fill all the cells in the array with the <Star /> 
                                    component. */}
                                {/* {Array(p.rating).fill(<Star className="star" />)} */}
                            </div>
                            <label className="info">Information</label>
                            <span className="username">Created by <b>username</b></span>
                            <span className="time">start time</span>
                            <span className="time">end time</span>
                        </div>
                    </Popup>
                    }
                    </>
                ))}
                {newPlace && (
                    <Popup
                        latitude={newPlace.lat} 
                        longitude={newPlace.long}
                        closeButton={true}
                        closeOnClick={false}
                        anchor="left"
                        onClose={() => setNewPlace(null)}
                    >
                        <div className="addEvents">
                            <form className="addEvent-form">
                                <label className="label">name: 
                                <input type="text" className="input-field" placeholder="Enter name..."></input>
                                </label>
                                <label className="label" id="cat-label">category:
                                <select id="options-list">
                                    <option id="cat-options" value="Arts/Culture">Music</option>
                                    <option id="cat-options" value="Sports">Sports</option>
                                    <option id="cat-options" value="Hangout">Music</option>
                                    <option id="cat-options" value="Music">Music</option>
                                </select>
                                </label>
                                <label className="label">address:</label><br></br>
                                <label className="label">start time:</label><br></br>
                                <label className="label">end time:</label><br></br>
                                <label className="label">description:</label><br></br>
                                <label className="label">likes:</label><br></br>
                                <label className="label">capacity:</label><br></br>
                                <button type ="submit" className="submit-btn">Add Event</button>
                            </form>
                        </div>
                    </Popup>
                )} 
            </ReactMapGL>
        </div>

    )
}

export default Map;
