import React, { useState } from 'react';
import './css/home.css';

function Event() {
    return(
        <li className="eventItem">

        </li>
    );
}

function Events() {
    var userEvent = '';
    var search = '';

    const [message, setMessage] = useState('');
    const [searchResults,setResults] = useState('');
    const [eventList, setEventList] = useState('');

    var _ud = localStorage.getItem('user_data');
    var ud = JSON.parse(_ud);
    var userId = ud.id;
    var firstName = ud.firstName;
    var lastName = ud.lastName;

    const addEvent = async event =>
    {
        event.preventDefault();

        var obj = {userId:userId, userEvent:userEvent.value}
        var js = JSON.stringify(obj);

        try
        {
            const response = await fetch('http://localhost:5000/api/addevent',
            {method:'POST',body:js,headers:{'Content-Type':'application/json'}});

            var txt = await response.text();
            var res = JSON.parse(txt);

            if( res.error.length > 0 )
            {
                setMessage("API Error: " + res.error);
            }
            else
            {
                setMessage('Event has been added');
            }
        }
        catch(e)
        {
            setMessage(e.toString());
        }
    };

    const searchEvent = async event =>
    {
        event.preventDefault();

        var obj = {userId:userId,search:search.value};
        var js = JSON.stringify(obj);

        try
        {
            const response = await fetch('http://localhost:5000/api/searchevents',
                {method:'POST',body:js,headers:{'Content-Type':'application/json'}});

            var txt = await response.text();
            var res = JSON.parse(txt);
            var _results = res.results;
            var resultText = '';
            for(var i = 0; i < _results.length; i++)
            {
                resultText += _results[i];
                if( i < _results.length - 1)
                {
                    resultText += ', ';
                }
            }
            setResults('Events(s) have been retrieved');
            setEventList(resultText);
        }
        catch(e)
        {
            alert(e.toString);
            setResults(e.toString());
        }

    };

    return(
        <div className="myEvents">
            <h1 id="eventsHeader">My Events</h1>
            <div id="postContainer">
                <input type="text" id="searchText" placeholder="Search Event" ref={(c) => search = c}/>
                <button type="button" id="searchButton" onClick={searchEvent}>Search</button> <br />
                <span id="eventSearchResult">{searchResults}</span>
                <p id="eventList">{eventList}</p><br /><br />
                <input type="text" id="addText" placeholder="Add Event" ref={(c) => userEvent = c} />
                <button type="button" id="postButton" onClick={addEvent}>Post</button> <br />
                <span id="eventAddResult">{message}</span>
            </div>
        </div>
    );
}

function TopBar() {
    return(
        <div class="topBar">
            <div class="linkContainer">
                <a className="topBarLink" id="homeLink">Home</a>
                <a className="topBarLink" id="calendarLink">Calendar</a>
            </div>
        </div>
    );
}

function HomePage() {
    return(
        <div>
            <TopBar />
            <Events />
        </div>
    );
};

function Home()
{
    return(
        <div className="HomePage">
            <HomePage />
        </div>
    );
};

export default Home;
