// import React, { useState } from 'react'
import './css/home.css'

import { faHome } from "@fortawesome/free-solid-svg-icons"
import { faCalendar } from "@fortawesome/free-solid-svg-icons"
import { FaMap } from "react-icons/fa"
import { faPlus } from "@fortawesome/free-solid-svg-icons"
import { useState } from 'react'
import { FaSearch } from "react-icons/fa"
import TopNav from '../components/TopNav';

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"

const initialList = [];


// function AddEvent() {
//     // this will redirect you to the page for adding events
//     // window.location.href="http://localhost:3000";
// }

function Event(item) {
    return(
        <li className="eventItem" id={item.id} >
            {item.name}
        </li>


    );
}

function Events() {

    const SearchText = ({ title, className, id }) => {
        return <input type="text" className={ className } id={ id } placeholder={ title }></input>
    }

    const [list, setList] = useState(initialList)
    const [name, setName] = useState('')

    function AddEvent() {
        // this will redirect you to the page for adding events
        // window.location.href="http://localhost:3000";
        const newList = list.concat(name);
        setList(newList);

    }

    return(
        <div className="myEvents">
            <h1 id="eventsHeader">My Events</h1>
            <div id="postContainer" onClick={AddEvent} value={name}>
                <button id="postButton"><FontAwesomeIcon icon={faPlus}/></button>
            </div>
            <div className="vl"></div>
            <div className="eventHolder">
                <ul className="eventList">
                    {list.map((item) =>(
                        <Event item={item}/>
                    ))}
                </ul>
            </div>
        </div>
    );
}

function OngoingEvents() {
    return (
        <div id="ongoing-Events">
            <h1 id="ongoing">Events Near U</h1>
            <ul className="ongoing-List">
                <Event />
            </ul>

        </div>
    );
}

function HomePage() {
    return(
        <div>
            <TopNav />
            <h1 id="feed">Dashboard</h1>
            <div id="search">
                <FaSearch id="search-btn" />
                <SearchText className ="text-field" id="search-bar" />
            </div>
            <OngoingEvents />
            <Events />
        </div>
    );
};

function Home()
{
    return(
        <div className="HomePage">
            <HomePage />
        </div>
    );
};

export default Home;
